import React, { useState } from 'react';
import axios from 'axios';
import styled from 'styled-components';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import L from 'leaflet';

// Fix for default markers in react-leaflet
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: require('leaflet/dist/images/marker-icon-2x.png'),
  iconUrl: require('leaflet/dist/images/marker-icon.png'),
  shadowUrl: require('leaflet/dist/images/marker-shadow.png'),
});

const Container = styled.div`
  min-height: calc(100vh - 40px);
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  padding: 20px;
  border-radius: 15px;
  margin-top: 20px;
`;

const Title = styled.h1`
  color: white;
  text-align: center;
  margin-bottom: 30px;
  font-size: 2.5rem;
  text-shadow: 2px 2px 4px rgba(0, 0, 0, 0.3);
`;

const Card = styled.div`
  background: rgba(255, 255, 255, 0.95);
  border-radius: 15px;
  padding: 25px;
  margin: 20px 0;
  box-shadow: 0 10px 30px rgba(0, 0, 0, 0.1);
  backdrop-filter: blur(10px);
`;

const Form = styled.form`
  display: flex;
  gap: 15px;
  margin-bottom: 30px;
  align-items: center;
`;

const Input = styled.input`
  flex: 1;
  padding: 15px;
  border: none;
  border-radius: 10px;
  font-size: 16px;
  background: rgba(255, 255, 255, 0.9);
  &:focus {
    outline: none;
    box-shadow: 0 0 0 3px rgba(102, 126, 234, 0.3);
  }
`;

const Button = styled.button`
  padding: 15px 30px;
  background: linear-gradient(45deg, #667eea, #764ba2);
  color: white;
  border: none;
  border-radius: 10px;
  font-size: 16px;
  font-weight: 600;
  cursor: pointer;
  transition: all 0.3s ease;
  
  &:hover {
    transform: translateY(-2px);
    box-shadow: 0 5px 15px rgba(102, 126, 234, 0.4);
  }
`;

const MapWrapper = styled.div`
  height: 500px;
  border-radius: 15px;
  overflow: hidden;
  box-shadow: 0 10px 30px rgba(0, 0, 0, 0.1);
`;

const Legend = styled.div`
  background: white;
  padding: 15px;
  border-radius: 10px;
  margin-top: 20px;
  box-shadow: 0 5px 15px rgba(0, 0, 0, 0.1);
`;

const LegendItem = styled.div`
  display: flex;
  align-items: center;
  margin: 5px 0;
  gap: 10px;
`;

const ColorBox = styled.div`
  width: 20px;
  height: 20px;
  border-radius: 4px;
  background-color: ${props => props.color};
`;

const ErrorMessage = styled.div`
  background: #f8d7da;
  color: #721c24;
  padding: 15px;
  border-radius: 10px;
  margin: 20px 0;
  border: 1px solid #f5c6cb;
`;

const LoadingMessage = styled.div`
  text-align: center;
  padding: 20px;
  color: #666;
  font-size: 16px;
`;

function Heatmap() {
  const [location, setLocation] = useState('');
  const [mapData, setMapData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [mapCenter, setMapCenter] = useState([39.8283, -98.5795]); // Default to center of US

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      const response = await axios.get('/heatmap');
      setMapData(response.data.heatmap_data);
      
      // Set map center based on the location data
      if (response.data.heatmap_data && response.data.heatmap_data.center) {
        setMapCenter([response.data.heatmap_data.center.lat, response.data.heatmap_data.center.lng]);
      }
    } catch (err) {
      setError('Error fetching heatmap data. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const getAQIColor = (aqi) => {
    if (aqi <= 50) return '#00e400'; // Good
    if (aqi <= 100) return '#ffff00'; // Moderate
    if (aqi <= 150) return '#ff7e00'; // Unhealthy for Sensitive Groups
    if (aqi <= 200) return '#ff0000'; // Unhealthy
    if (aqi <= 300) return '#8f3f97'; // Very Unhealthy
    return '#7e0023'; // Hazardous
  };

  const getAQICategory = (aqi) => {
    if (aqi <= 50) return 'Good';
    if (aqi <= 100) return 'Moderate';
    if (aqi <= 150) return 'Unhealthy for Sensitive Groups';
    if (aqi <= 200) return 'Unhealthy';
    if (aqi <= 300) return 'Very Unhealthy';
    return 'Hazardous';
  };

  const createCustomIcon = (aqi) => {
    const color = getAQIColor(aqi);
    return L.divIcon({
      html: `<div style="
        background-color: ${color};
        width: 30px;
        height: 30px;
        border-radius: 50%;
        border: 3px solid white;
        display: flex;
        align-items: center;
        justify-content: center;
        color: ${aqi > 100 ? 'white' : 'black'};
        font-weight: bold;
        font-size: 12px;
        box-shadow: 0 2px 5px rgba(0,0,0,0.3);
      ">${aqi}</div>`,
      className: 'custom-marker',
      iconSize: [30, 30],
      iconAnchor: [15, 15]
    });
  };

  const legendItems = [
    { color: '#00e400', label: '0-50: Good' },
    { color: '#ffff00', label: '51-100: Moderate' },
    { color: '#ff7e00', label: '101-150: Unhealthy for Sensitive Groups' },
    { color: '#ff0000', label: '151-200: Unhealthy' },
    { color: '#8f3f97', label: '201-300: Very Unhealthy' },
    { color: '#7e0023', label: '301+: Hazardous' }
  ];

  return (
    <Container>
      <Title>🗺️ Air Quality Heatmap</Title>
      
      <Card>
        <Form onSubmit={handleSubmit}>
          <Input
            type="text"
            value={location}
            onChange={(e) => setLocation(e.target.value)}
            placeholder="Enter city or region name"
            required
          />
          <Button type="submit" disabled={loading}>
            {loading ? 'Loading...' : 'Show Heatmap'}
          </Button>
        </Form>

        {error && <ErrorMessage>{error}</ErrorMessage>}

        {loading && <LoadingMessage>Loading air quality data...</LoadingMessage>}

        {mapData && (
          <>
            <MapWrapper>
              <MapContainer
                center={mapCenter}
                zoom={10}
                style={{ height: '100%', width: '100%' }}
              >
                <TileLayer
                  attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
                  url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                />
                {mapData.locations && mapData.locations.map((location, index) => (
                  <Marker
                    key={index}
                    position={[location.lat, location.lng]}
                    icon={createCustomIcon(location.aqi)}
                  >
                    <Popup>
                      <div>
                        <h3>{location.name}</h3>
                        <p><strong>AQI:</strong> {location.aqi}</p>
                        <p><strong>Category:</strong> {getAQICategory(location.aqi)}</p>
                        <p><strong>Main Pollutant:</strong> {location.main_pollutant || 'N/A'}</p>
                        <p><strong>Last Updated:</strong> {location.timestamp || 'N/A'}</p>
                      </div>
                    </Popup>
                  </Marker>
                ))}
              </MapContainer>
            </MapWrapper>

            <Legend>
              <h3>AQI Legend</h3>
              {legendItems.map((item, index) => (
                <LegendItem key={index}>
                  <ColorBox color={item.color} />
                  <span>{item.label}</span>
                </LegendItem>
              ))}
            </Legend>
          </>
        )}
      </Card>
    </Container>
  );
}

export default Heatmap;
